pragma solidity ^0.4.21;

import "./crowdsale/distribution/FinalizableCrowdsale.sol";
import "./crowdsale/validation/CappedCrowdsale.sol";
import "./crowdsale/distribution/RefundableCrowdsale.sol";
import "./crowdsale/emission/MintedCrowdsale.sol";
import "./token/ERC20/StandardToken.sol";

contract CxNtoken {
    function totalSupply() public view returns (uint256);
    function balanceOf(address who) public view returns (uint256);
    function transfer(address to, uint256 value) public returns (bool);
    event Transfer(address indexed from, address indexed to, uint256 value);
}

contract CXNcontract is CappedCrowdsale, RefundableCrowdsale {

    function CXNcontract(uint256 _openingTime, uint256 _closingTime, uint256 _rate, address _wallet, uint256 _cap, StandardToken _token, uint256 _goal) public 
        Crowdsale(_rate, _wallet, _token)
    CappedCrowdsale(_cap)
    TimedCrowdsale(_openingTime, _closingTime)
    RefundableCrowdsale(_goal) 
    {
        //As goal needs to be met for a successful crowdsale
        //the value needs to less or equal than a cap which is limit for accepted funds
        require(_goal <= _cap);
    }
}
